mapper.select:
select T1.softwareName, T1.APKName, T1.softwareSize, (SELECT valueName FROM data_dictionary WHERE typeCode = 'APP_FLATFORM' AND valueId=T1.flatformId) AS flatformName, (SELECT categoryName FROM app_category WHERE app_category.id=T1.categoryLevel1) AS categoryLevel1Name, (SELECT categoryName FROM app_category WHERE app_category.id=T1.categoryLevel2) AS categoryLevel2Name, (SELECT categoryName FROM app_category WHERE app_category.id=T1.categoryLevel3) AS categoryLevel3Name, (SELECT valueName FROM data_dictionary WHERE typeCode = 'APP_STATUS' AND valueId=T1.status) AS statusName, T1.downloads, (SELECT versionNo FROM app_version WHERE app_version.id=T1.versionId) AS versionNo from app_info AS T1
AND softwareName LIKE CONCAT('%',#{appinfo.softwareName},'%')
AND status =#{appinfo.status}
AND flatformId =#{appinfo.flatformId}
AND categoryLevel1 =#{appinfo.categoryLevel1}
AND categoryLevel2 =#{appinfo.categoryLevel2}
AND categoryLevel3 =#{appinfo.categoryLevel3}

select
    COUNT(1)
from
  app_info


        AND softwareName LIKE CONCAT('%',#{softwareName},'%')


        AND status =#{status}


        AND flatformId =#{flatformId}


        AND categoryLevel1 =#{categoryLevel1}


        AND categoryLevel2 =#{categoryLevel2}


        AND categoryLevel3 =#{categoryLevel3}
